import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { Clock, MapPin, Phone, Calendar, X } from 'lucide-react';

const ClinicHours = () => {
    const [ref, inView] = useInView({ threshold: 0.3, triggerOnce: true });


  const schedule = [
    { day: 'Monday', time: '10:00 AM – 1:00 PM, 4:00 PM – 7:00 PM', status: 'open' },
    { day: 'Tuesday', time: '10:00 AM – 1:00 PM, 4:00 PM – 7:00 PM', status: 'open' },
    { day: 'Wednesday', time: '10:00 AM – 1:00 PM, 4:00 PM – 7:00 PM', status: 'open' },
    { day: 'Thursday', time: '10:00 AM – 1:00 PM, 4:00 PM – 7:00 PM', status: 'open' },
    { day: 'Friday', time: '10:00 AM – 1:00 PM, 4:00 PM – 7:00 PM', status: 'open' },
    { day: 'Saturday', time: '10:00 AM – 1:00 PM, 4:00 PM – 7:00 PM', status: 'open' },
    { day: 'Sunday', time: 'Closed', status: 'closed' }
  ];

  const getCurrentDay = () => {
    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    return days[new Date().getDay()];
  };

  const isToday = (day) => day === getCurrentDay();

  return (
    <section id="hours" className="section-padding bg-gray-50 dark:bg-gray-800">
      <div className="container-custom">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-gray-900 dark:text-white mb-6">
            Clinic <span className="text-gradient">Hours</span>
          </h2>
          <p className="text-xl text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            Plan your visit with our convenient schedule. We're here to help you on your healing journey.
          </p>
        </motion.div>

        <div className="grid lg:grid-cols-2 gap-12 items-start">
          {/* Schedule Card */}
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.2 }}
            className="bg-white dark:bg-gray-800 rounded-2xl shadow-xl p-8"
          >
            <div className="flex items-center space-x-3 mb-8">
              <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                <Clock className="w-6 h-6 text-green-600" />
              </div>
                             <div>
                 <h3 className="text-2xl font-bold text-gray-900 dark:text-white">Opening Hours</h3>
                 <p className="text-gray-600 dark:text-gray-300">Monday to Saturday</p>
               </div>
            </div>

            <div className="space-y-4">
              {schedule.map((item, index) => (
                <motion.div
                  key={item.day}
                  initial={{ opacity: 0, y: 20 }}
                  animate={inView ? { opacity: 1, y: 0 } : {}}
                  transition={{ duration: 0.6, delay: 0.4 + index * 0.1 }}
                                     className={`flex items-center justify-between p-4 rounded-lg border-2 transition-all duration-300 ${
                     isToday(item.day)
                       ? 'border-green-500 bg-green-50 dark:bg-green-900/20'
                       : item.status === 'closed'
                       ? 'border-red-200 bg-red-50 dark:bg-red-900/20'
                       : 'border-gray-200 dark:border-gray-600 hover:border-green-300 hover:bg-gray-50 dark:hover:bg-gray-700'
                   }`}
